import { useState, useEffect } from "react";
import { bunzz } from "bunzz-sdk";

import logo from "./logo.svg";
import "./App.css";

const API_KEY = "91965616-bf7e-4bde-97ea-a39c31980635";
const DAPP_ID = "d3779a9c-bf13-4842-b3e7-2328ffa2d7d4";

const App = () => {
  const [value, setValue] = useState(0);
  const [userAccountAddress, setUserAccountAddress] = useState("hoge");

  const handleChange = (e) => setValue(e.target.value);

  useEffect(() => {
    const initialize = async () => {
      try {
        const contractHandler: Handler = await bunzz.initializeHandler({
          dappId: DAPP_ID,
          apiKey: API_KEY,
        });
        const userAddress = await contractHandler.getSignerAddress();

        console.log(userAddress);
        setUserAccountAddress(userAddress);
      } catch (error) {
        console.error(error);
      }
    };

    initialize();
  }, []);

  const submit = async () => {
    console.log(value);
    console.log(userAccountAddress);
  };

  return (
    <div className="App App-header">
      <input value={value} onChange={handleChange} type="text" />
      <button onClick={submit}>mint</button>
    </div>
  );
};

export default App;
