import { useState } from "react";
import { bunzz } from "bunzz-sdk";

import logo from "./logo.svg";
import "./App.css";

const API_KEY = "91965616-bf7e-4bde-97ea-a39c31980635";
const DAPP_ID = "d3779a9c-bf13-4842-b3e7-2328ffa2d7d4";

const handler = bunzz.initializeHandler({
  apikey: API_KEY,
  dappId: DAPP_ID,
});

const ERC20 = handler.getContract("ERC20");

const App = () => {
  const [value, setValue] = useState(0);

  const handleChange = (e) => setValue(e.target.value);

  const submit = async () => {
    console.log(value);
    const signerAddress = await handler.getAddress();
    console.log(signerAddress);
  };

  return (
    <div className="App App-header">
      <input value={value} onChange={handleChange} type="text" />
      <button onClick={submit}>mint</button>
    </div>
  );
};

export default App;
